import React from 'react';
import '../hoja-de-estilos/Card.css';
import { Container, Row, Col } from 'react-bootstrap';
import * as images from '../Assets/climate/climate_images.js';

function Card({
  temp,
  max,
  min,
  name,
  img,
  onClose,
  handleOnDragEnd,
  humidity,
  lon,
  lat,
  date,
  description,
  wind,
  pressure,
}) {
  /* set different images according to description: */
  if (description === 'clear sky') {
    img = images.sunny;
  } else if (description === 'few clouds') {
    img = images.cloudy;
  } else if (description === 'scattered clouds') {
    img = images.cloudy;
  } else if (description === 'broken clouds') {
    img = images.cloudy;
  } else if (description === 'shower rain') {
    img = images.rainy;
  } else if (description === 'rain') {
    img = images.rainy;
  } else if (description === 'thunderstorm') {
    img = images.storm;
  } else if (description === 'snow') {
    img = images.snow;
  } else if (description === 'mist') {
    img = images.mist;
  } else if (description === 'overcast clouds') {
    img = images.cloudy;
  } else if (description === 'light rain') {
    img = images.rainy;
  } else if (description === 'moderate rain') {
    img = images.rainy;
  } else if (description === 'heavy intensity rain') {
    img = images.rainy;
  } else if (description === 'very heavy rain') {
    img = images.rainy;
  } else if (description === 'extreme rain') {
    img = images.rainy;
  } else if (description === 'freezing rain') {
    img = images.rainy;
  } else if (description === 'light intensity shower rain') {
    img = images.rainy;
  } else if (description === 'heavy intensity shower rain') {
    img = images.rainy;
  } else if (description === 'ragged shower rain') {
    img = images.rainy;
  } else if (description === 'light snow') {
    img = images.snow;
  } else if (description === 'heavy snow') {
    img = images.snow;
  } else if (description === 'sleet') {
    img = images.snow;
  } else if (description === 'shower sleet') {
    img = images.snow;
  } else if (description === 'light rain and snow') {
    img = images.snow;
  } else if (description === 'rain and snow') {
    img = images.snow;
  } else if (description === 'light shower snow') {
    img = images.snow;
  } else if (description === 'shower snow') {
    img = images.snow;
  } else if (description === 'heavy shower snow') {
    img = images.snow;
  } else if (description === 'light intensity drizzle') {
    img = images.rainy;
  } else if (description === 'drizzle') {
    img = images.rainy;
  } else if (description === 'heavy intensity drizzle') {
    img = images.rainy;
  } else if (description === 'light intensity drizzle rain') {
    img = images.rainy;
  } else if (description === 'drizzle rain') {
    img = images.rainy;
  } else if (description === 'heavy intensity drizzle rain') {
    img = images.rainy;
  } else if (description === 'shower rain and drizzle') {
    img = images.rainy;
  } else if (description === 'heavy shower rain and drizzle') {
    img = images.rainy;
  } else if (description === 'shower drizzle') {
    img = images.rainy;
  } else if (description === 'light intensity drizzle') {
    img = images.rainy;
  } else if (description === 'heavy intensity drizzle') {
    img = images.rainy;
  } else if (description === 'light intensity drizzle rain') {
    img = images.rainy;
  } else if (description === 'drizzle rain') {
    img = images.rainy;
  } else {
    img = images.sunny;
  }

  return (
    <div className="flip-card">
      <div className="flip-card-inner">
        <div className="flip-card-front">
          <Container className="container.front">
            <h3
              style={{
                marginTop: '15px',
              }}
            >
              {name}
            </h3>
            <p> {`Today - ${date}`}</p>
            {/* <img
              src={`http://openweathermap.org/img/wn/${img}@2x.png`}
              alt={'image_weather'}
              style={{
                height: '45%',
                width: '45%',
                filter: 'invert(100%)',
              }}
            /> */}
            {
              {
                /* if description === "clear sky" then use image.sunny: */
              }
            }
            <p>{description}</p>
            <h2>{`${temp}°C`}</h2>
          </Container>
        </div>
        <div className="flip-card-back">
          <Row xs={2} md={2} lg={2}>
            <Col xs={7} md={7} lg={7}>
              <Container className="container.back">
                <Row xs={1} md={1} lg={1}>
                  <Col>
                    <p>{`Min: ${min}°C`}</p>
                  </Col>
                </Row>
                <Row xs={1} md={1} lg={1}>
                  <Col>
                    <p>{`Max: ${max}°C`}</p>
                  </Col>
                </Row>
                <Row xs={1} md={1} lg={1}>
                  <Col>
                    <p>{`Humidity: ${humidity}%`}</p>
                  </Col>
                </Row>
                <Row xs={1} md={1} lg={1}>
                  <Col>
                    <p>{`Wind: ${wind}`}</p>
                  </Col>
                </Row>
                <Row xs={1} md={1} lg={1}>
                  <Col>
                    <p>{`Pressure: ${pressure}`}</p>
                  </Col>
                </Row>
              </Container>
            </Col>
            <Col xs={1} md={1} lg={1}>
              <img
                src={`http://openweathermap.org/img/wn/${img}@2x.png`}
                alt={'image_weather'}
                style={{
                  paddingRight: '20px',
                  filter: 'invert(100%)',
                }}
              />
            </Col>
          </Row>
          <Row className="button_row">
            <Col xs={6} md={6} lg={6}>
              <button>
                <span className="circle" aria-hidden="true">
                  <span className="arrow_icon"></span>
                </span>
                <span className="button_text">View Week</span>
              </button>
            </Col>
            <Col xs={6} md={6} lg={6}>
              <button className="close_button" onClick={onClose}>
                <svg
                  xmlns="http://www.w3.org/2000/svg"
                  width="30"
                  height="30"
                  fill="currentColor"
                  class="bi bi-trash3-fill"
                  viewBox="0 0 16 16"
                >
                  <path d="M11 1.5v1h3.5a.5.5 0 0 1 0 1h-.538l-.853 10.66A2 2 0 0 1 11.115 16h-6.23a2 2 0 0 1-1.994-1.84L2.038 3.5H1.5a.5.5 0 0 1 0-1H5v-1A1.5 1.5 0 0 1 6.5 0h3A1.5 1.5 0 0 1 11 1.5Zm-5 0v1h4v-1a.5.5 0 0 0-.5-.5h-3a.5.5 0 0 0-.5.5ZM4.5 5.029l.5 8.5a.5.5 0 1 0 .998-.06l-.5-8.5a.5.5 0 1 0-.998.06Zm6.53-.528a.5.5 0 0 0-.528.47l-.5 8.5a.5.5 0 0 0 .998.058l.5-8.5a.5.5 0 0 0-.47-.528ZM8 4.5a.5.5 0 0 0-.5.5v8.5a.5.5 0 0 0 1 0V5a.5.5 0 0 0-.5-.5Z" />
                </svg>
              </button>
            </Col>
          </Row>
        </div>
      </div>
    </div>
  );
}

export default Card;
