import React from 'react';
import Card from './Card';
import '../hoja-de-estilos/Cards.css';
import { DragDropContext, Droppable, Draggable } from 'react-beautiful-dnd';

function Cards({ cities, onClose, handleOnDragEnd }) {
  return (
    <div className="allCards">
      <DragDropContext onDragEnd={handleOnDragEnd}>
        <Droppable droppableId="cities_list">
          {(provided) => (
            <ul
              className="cities_list"
              style={{ listStyleType: 'none' }}
              {...provided.droppableProps}
              ref={provided.innerRef}
            >
              {cities.map((ciudad, index) => (
                <Draggable
                  key={ciudad.id}
                  draggableId={ciudad.id}
                  index={index}
                >
                  <Card
                    temp={ciudad.temp}
                    max={ciudad.max}
                    min={ciudad.min}
                    name={ciudad.name}
                    img={ciudad.img}
                    onClose={() => onClose(ciudad.id)}
                    handleOnDragEnd={() => handleOnDragEnd()}
                    key={ciudad.id}
                    humidity={ciudad.humidity}
                    lon={ciudad.lon}
                    lat={ciudad.lat}
                  />
                </Draggable>
              ))}
            </ul>
          )}
        </Droppable>
      </DragDropContext>
    </div>
  );
}

export default Cards;
