import React, { useState, useEffect } from 'react';
/* create component for more detail: */
const DetailedCard = ({ name, lon, lat }) => {
  const [week, setWeek] = useState([]);
  let apiKey = '4ae2636d8dfbdc3044bede63951a019b';

  function searchWeek(lon, lat) {
    fetch(
      ` https://pro.openweathermap.org/data/2.5/forecast/hourly?lat=${lat}&lon=${lon}&appid=${apiKey}`
    )
      .then((r) => r.json())
      .then((recurso) => {
        const detailedWeek = {
          first_day: recurso.list.temp,
        };
        setWeek(detailedWeek);
        console.log(detailedWeek);
      });
  }
  useEffect(() => {
    searchWeek(lon, lat);
  }, [lon, lat]);

  return (
    <div>
      <p>Hola</p>
    </div>
  );
};

export default DetailedCard;
